def test__do_graph(field, graph):
    assert do_graph(field) == graph


def test__find_path__start_and_goal_are_different(graph, start, goal):
    assert find_path(graph, start, goal) == [(1, 2), (1, 1), (2, 1)]


def test__find_path__start_and_goal_are_the_same(graph, start):
    assert find_path(graph, start, goal=start) == [start]


def test__find_path__goal_out_of_bounds(graph, start, goal=(21, 12)):
    with pytest.raises(KeyError):
        find_path(graph, start, goal)
